mapper.select:
select count(*) as "count"
  from (
    select distinct document_no
      from approval a
      join document d using(document_no)
     where a.user_no = #{userNo}
       and reference_status = 1
   )

mapper.delete:
delete from document where document_no in
#{no}
and status = 2

mapper.select:
select count(*) as "count"
  from (
    select distinct document_no
      from approval a
      join document d using(document_no)
     where a.user_no = #{userNo}
       and reference_status = 1
       and document_title like '%'||#{keyword}||'%'
   )

