mapper.update:
update document
   set document_title = #{document.documentTitle}
     , document_content = #{document.documentContent}
     , origin_name = #{document.originName}
     , change_name = #{document.changeName}
     , approval_count = (select count(*)
                           from approval
                          where document_no = #{document.documentNo}
                            and reference_status is null
                        )
     , progress = 1


     		, status = 2


     		, status = 1


 where document_no = #{document.documentNo}

mapper.insert:
insert into
  plan (
  	   document_no
  	 , plan_start
       )
values (
       #{document.documentNo}
     , #{plan.planStart}
       )

	insert into
   absence (
	  	   document_no
	  	 , absence_date
	       )
	values (
	       #{document.documentNo}
	     , #{absence.absenceDate}
	       )

	insert into
  vacation (
	  	   document_no
	  	 , vacation_category
	  	 , vacation_start
	  	 , vacation_end
	  	 , vacation_count
	       )
	values (
	       #{document.documentNo}
	     , #{vacation.vacationCategory}
	     , #{vacation.vacationStart}
	     , #{vacation.vacationEnd}
	     , to_date(#{vacation.vacationEnd},'YYYY-MM-DD') - to_date(#{vacation.vacationStart},'YYYY-MM-DD')
	       )

	insert into
      work (
	  	   document_no
	  	 , work_start
	  	 , work_end
	  	 , work_date
	  	 , work_use
	       )
	values (
	       #{document.documentNo}
	     , #{overtime.workStart}
	     , #{overtime.workEnd}
	     , #{overtime.workDate}
	     , round((to_date(#{overtime.workEnd},'HH24:MI') - to_date(#{overtime.workStart},'HH24:MI')) *24)
	       )

mapper.select:
select plan_start
  from plan
 where document_no = #{no}

select absence_date
  from absence
 where document_no = #{no}

