mapper.insert:
insert
  into board
  (
    board_no
  , user_no
  , board_type
  , board_title
  , board_content
  , top_exp
  , dep_name
  , save_status
  )
  values
  (
    seq_board.nextval
  , #{userNo}
  , #{boardType}
  , #{boardTitle}
  , #{boardContent}
  , #{topExp}
  , #{depName}
  , 'Y'
  )

mapper.update:
update
	   board
   set board_title = #{boardTitle}
     , board_content = #{boardContent}
     , create_date = sysdate
     , top_exp = #{topExp}
 where board_no = #{boardNo}

