mapper.insert:
insert
  into mail
     (
       mail_no
     , mail_title
     , mail_content
     , sender
     , receiver
     , mail_ref
     )
values
	 (
	   seq_mail.nextval
	 , #{mailTitle}
	 , #{mailContent}
	 , #{sender}
	 , #{receiver}
	 , #{mailRef}
	 )

insert
  into mail_status
     (
       mail_no
     , user_mail
     , user_name
     , mail_type
     )
values
	 (
	   seq_mail.currval
	 , #{userMail}
	 , #{userName}
	 , #{mailType}
	 )

insert
  into mail
     (
       mail_no
     , mail_title
     , mail_content
     , sender
     , receiver
     , mail_ref
     , send_status
     )
values
	 (
	   seq_mail.nextval
	 , #{mailTitle}
	 , #{mailContent}
	 , #{sender}
	 , #{receiver}
	 , #{mailRef}
	 , 'N'
	 )

mapper.update:
update
	   mail
   set 
       mail_title = #{mailTitle}
     , mail_content = #{mailContent}
     , receiver = #{receiver}
     , mail_ref = #{mailRef}
 where mail_no = #{mailNo}
   and sent_status = 'N'

update
	   mail
   set 
       mail_title = #{mailTitle}
     , mail_content = #{mailContent}
     , receiver = #{receiver}
     , mail_ref = #{mailRef}
     , send_status = 'Y'
 where mail_no = #{mailNo}
   and sent_status = 'N'
