mapper.update:
update
		member
	set   user_email = #{userEmail}
		, phone = #{phone}
		, birthday = #{birthday}
		, modify_date = sysdate
	where email = #{email}

update
	member
set prof_img = #{profImg}
	,modify_date = sysdate
where email = #{email}

update
	member
set user_pwd = #{userPwd}
,	modify_date = sysdate
where email = #{email}

mapper.insert:
insert
	into member
	(
		user_no
		,email
		,user_pwd
		,user_name
		,phone
		,user_email
		,dep_name
		,job_name
		,create_date
		,modify_date
	)
	values
	(
		LPAD(SEQ_MEMBER.NEXTVAL, 5, '0')
		,#{email}
		,#{userPwd}
		,#{userName}
		,#{phone}
		,#{userEmail}
		,#{depName}
		,#{jobName}
		,sysdate
		,sysdate
	)

mapper.update:
